package com.example.demo.controller;import com.example.demo.domain.db.pojo.UserDO;import com.example.demo.domain.db.mapper.UserMapper;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;import javax.annotation.Resource;@Controller@ResponseBody@RequestMapping("user")public class LoginController{    @Resource    private UserMapper userMapper;    @RequestMapping(value = "/ping",method = RequestMethod.GET)    public String ping(){        return "pong";    }    @RequestMapping(value = "/getUserName",method = RequestMethod.GET)    public String getUserName(){        UserDO userDO = userMapper.selectByPrimaryKey(1L);        return userDO.getName();    }}